import React from 'react'
import { useEffect } from "react";
import { useLocation, useParams } from "react-router-dom";

import axios from "axios";

const Post=(props)=> {
    
    const [post, setPost] = React.useState(null);
    const params = useParams();
    const prodId = params.id;
//console.log(prodId );
    //let a=this.props.params.id
  //console.log(this.props.params.id)
    React.useEffect(() => {
      axios.get(`http://localhost:5000/api/getOnePointHelp/`+prodId).then((response) => {
        setPost(response.data);
      });
    }, []);
  
   
  
    return (
      <div>
        <div class="card" > 
  <h2 class="card-header">{props.person.name}<h6>{props.person.description}</h6></h2>
  <div class="card-body">
    <h6 class="card-title">Виды помощи: {props.person.listThings}</h6>
    <p class="clip">{props.person.region}</p>
    <p><small>Email: {props.person.email}</small></p> 
    <p><small>Телефое: {props.person.phone} {props.person.nameBoss}  </small></p> 
    <p><small>Адрес: {props.person.city} {props.person.address} </small></p> 

    <a href={'/gum/'+props.person._id} class="btn btn-primary">Подробнее</a>
  </div>
</div>
        
      </div>
    );
  }


  export default Post
