import React, { useState } from 'react'
import { useEffect } from "react";
import s from './module.css'

import { renderMatches, useParams } from "react-router-dom";

import axios from "axios";

const Post=(props)=> {
    const params = useParams();
    const prodId = params.id;


    const [post, setPost] = useState(null);
    

    //let a=this.props.params.id
  //console.log(this.props.params.id)
    useEffect(() => {
      axios.get(`http://localhost:5000/api/getOnePointHelp/`+prodId).then((response) => {
        setPost(response.data);
      });
    }, []);
  
   
  
    return (
<div>{post.name}
<p><small>Email: {post.email}</small></p> 
    <p><small>Телефое: {post.phone} {post.nameBoss}  </small></p> 
    <p><small>Адрес: {post.city} {post.address} </small></p> </div>

    );
  }


  export default Post
