import React from "react";
import PostHelp from "./posthelpa";
import { useState } from "react";
import { useEffect } from "react";
import { getAllPointHelp } from "../http/feth";

const ListHelp = (props) => {
  const [persons, setPersons] = useState([])
  const [sort, setSort] = useState()


  useEffect(() => {
    getAllPointHelp().then(data => setPersons(data))
  }, [])

  return (<div>

    <div class="dropdown">
      <button class="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
        Dropdown button
      </button>
      <select value={sort}
        onChange={() => setSort()}>
        <option value='name'>По имени</option>
        <option value='like'>По лайкам</option>
        <option value='views'>По просмотрам</option>

      </select>
    </div>



    <ul>
      {persons.map(person => <PostHelp person={person} />)}
    </ul>
  </div>
  )

}
export default ListHelp
