import React, { useEffect, useState, useContext,createContext} from "react";
import s from './Header.module.css'
import axios from "axios";
import {Context} from "./../../index";
import jwt_decode from "jwt-decode";
import {check, login, registration} from './../http/userApi'
import {NavLink, useNavigate} from "react-router-dom";
import {observer} from "mobx-react-lite";
import { tryremovepassvord } from "../http/feth";



const RecoveryPassword=observer(()=>{
   const user=useContext(Context)

   const [email, setEmail] = useState('qq@gmail.com')


  
    const click = async () => {
        try {
            let data;
            data=await tryremovepassvord(email);



        } catch (e) {
           alert(e.response)
        }

    }

    


    return <header className={s.header}>
                <div>
                <form>
                        <div >
                        <label for="exampleInputPassword1" class="form-label">Password</label>
    <input type="password" class="form-control" id="exampleInputPassword1"
      value={email}
      onChange={e => setEmail(e.target.value)}
    ></input>
                            
                        </div>
                      
                        
                        </form>
                      
                        <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal" onClick={click}>востановить паррноль</button>
                      
           


                      <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                        <div class="modal-dialog">
                          <div class="modal-content">
                            <div class="modal-header">
                              <h5 class="modal-title" id="exampleModalLabel">Провертье почтуу</h5>
                              <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>
                            <div class="modal-body">
                            перейлите на почту чтобы востановить пароль
                            </div>
                          
                          </div>
                        </div>
                      </div>
                      

                        

                </div>
    </header>
})

export default RecoveryPassword
