import axios from "axios";
import React, { useState } from "react";
import { $authHost } from "../../http";
import ListAssist from "./ListAssist";
import ListNeedhelp from "./ListNeedhelp";


export default class Profil extends React.Component {
    state = {
        persons: [],
        nhs: []
    }



    componentDidMount() {
        $authHost.get(`http://localhost:5000/api/getAsistPerson`)
            .then(res => {
                const persons = res.data;
                this.setState({ persons });

            })
        $authHost.get(`http://localhost:5000/api/getNeedHelpPerson`)
            .then(res => {
                const nhs = res.data;
                this.setState({ nhs });

            })
    }




    render() {
        return (<div>
            <div>

                Ваша помощь
                <ul>
                    {this.state.persons.map(person => <ListAssist person={person} />)}


                </ul>
            </div>

            <div>
                Нужна помошт
                <ul>
                    {this.state.nhs==0? <h1>idofkposdfkod</h1>:this.state.nhs.map(nh => <ListNeedhelp nh={nh} />)}
                    


                </ul>
            </div>







        </div>
        )
    }
}
