import Error from './../Error/error'
import React from 'react'
import NeedHelp from './../exampl/Header'
import Post from '../posthelper/Post'
import Header from './../Header/Header'
import { Route , Routes, Navigate} from 'react-router-dom';
import Login from '../pages/login';
import ListHelp from '../posthelper/listhelp';
import FullNeedHelp from './../exampl/FullNeedHelp'
import Headerr from './../exampl/Header';
import CanHelp from '../posthelper/canhelp'
import FullCanHelp from '../posthelper/FullCanHelp'
import CanHelpA from '../AuthComponent/CanHelp/CanHelpA'
import AddCanHelp from '../AuthComponent/CanHelp/AddCanHelp'
import NeedHelpA from'./../AuthComponent/NeedHelp/NeedHelpA'
import AddNeedHelp from '../AuthComponent/NeedHelp/AddNeedHelp'
import { useContext } from 'react'
import { Context } from '../..'


export const publicRoutes = [
    {
        path: "/main",
        Element: <Headerr/>
    },
    {
        path: "/gum",
        Element: <ListHelp />
    },]

const AppRouter=()=>{
const{user}=useContext(Context)
   
return(
        <Routes> 
      

      {publicRoutes.map(({path, Element}) =>
                <Route key={path} path={path} element={Element} exact/>
            )}

   
      
        <Route exact path="/ch" element={<CanHelp />}/>
        <Route exact path="/nh" element={<NeedHelp />}/>
        <Route exact path="/login" element={<Login />}/>
        
        <Route exact path="/nh/:id" element={<FullNeedHelp />}/>

        <Route exact path="/gum/:id" element={<Post/>}/>
        <Route exact path="/ch/:id" element={<FullCanHelp />}/>
        <Route exact path="/nh/:id" element={<FullNeedHelp />}/>

        <Route exact path="/nha" element={<NeedHelpA />}/>
        <Route exact path="/addneedhelp" element={<AddNeedHelp/>}/>

        <Route exact path="/cha" element={<CanHelpA />}/>
        <Route exact path="/addcanhelp" element={<AddCanHelp/>}/>



        <Route
                            path="*"
                            element={<Error to="/Error" />}
                        />
        </Routes>

    )}



export default AppRouter
