import React, { useEffect, useState, useContext,createContext} from "react";
import s from './Header.module.css'
import axios from "axios";
import {Context} from "./../../index";
import jwt_decode from "jwt-decode";
import {check, login, registration} from './../http/userApi'
import {NavLink, useNavigate} from "react-router-dom";
import {observer} from "mobx-react-lite";



const Login=observer(()=>{
   const user=useContext(Context)
   //const isLogin = location.pathname === LOGIN_ROUTE
   const history =useNavigate();
   const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

  
    const click = async () => {
        try {
            let data;
            if (true) {
                data = await login(email, password);
                console.log(data)
                data=await check()
                console.log(data)
                
                window.location.reload()
                
            } else {
                data = await registration(email, password);
                console.log(data)
                
            }
            user.setUser(user)
            user.setIsAuth(true)
            history.push('/')
            


        } catch (e) {
           alert(e.response)
        }

    }

    const vhod = async () => {
        try {
            let data;
            if (true) {
                setEmail('qq@gmail.com')
                setPassword('1234')
                console.log(email)
                console.log(password)
                data = await login(email, password);
                console.log(data)
                data=await check()
                console.log(data)
                
                window.location.reload()
                
            } else {
                data = await registration(email, password);
                console.log(data)
                
            }
            user.setUser(user)
            user.setIsAuth(true)
            history.push('/')
            


        } catch (e) {
           alert(e.response)
        }

    }



    return <header className={s.header}>
                <div>
                <form>
                        <div >
                            <input  
                            
                            value={email}
                            onChange={e => setEmail(e.target.value)}></input>
                            
                        </div>
                        <div>
                            <input
                            value={password}
                            onChange={e => setPassword(e.target.value)}></input>
                        </div>
                     
                        
                        </form>
                        <NavLink to="../main"><button onClick={click}>Submit</button></NavLink>
                        <NavLink to="../main"><button
                        onChange={e => setPassword('1234')}
                        onClick={vhod}>Voiti</button></NavLink>
                        

                </div>
    </header>
})

export default Login
