import jwt_decode from "jwt-decode";
import {check, login, registration} from './../http/userApi'
import {NavLink, useNavigate, useParams} from "react-router-dom";
import {observer} from "mobx-react-lite";
import { tryremovepassvord } from "../http/feth";



const RemovePassword=observer(()=>{
   const user=useContext(Context)
   const {id} =useParams()
   console.log(id)   
   const [email, setEmail] = useState('')
   const [password, setPassword] = useState('')


  
    const click = async () => {
        try {
            let data;
            data=await removepassvord(email,password);



        } catch (e) {
           alert(e.response)
        }

    }

    


    return <header className={s.header}>
                <div>
                <form>
                        <div >
                            <input  
                            
                            value={email}
                            onChange={e => setEmail(e.target.value)}></input>
                              <input  
                            
                            value={password}
                            onChange={e => setPassword(e.target.value)}></input>
                        </div>
                      
                        
                        </form>
                      
                        <button  onClick={click}>востановить паррноль</button>
                          

                        

                </div>
    </header>
})

export default RemovePassword
