import React from 'react'
import { useEffect } from "react";
import s from './module.css'

import { useLocation, useParams } from "react-router-dom";

import axios from "axios";

const Post=(props)=> {
    
    const [post, setPost] = React.useState(null);
    const params = useParams();
    const prodId = params.id;
console.log(prodId );
    //let a=this.props.params.id
  //console.log(this.props.params.id)
    React.useEffect(() => {
      axios.get(`http://localhost:5000/api/getOnePointHelp/`+prodId).then((response) => {
        setPost(response.data);
      });
    }, []);
  
   
  
    return (
       <div class="card" > 
  <h2 class="card-header">{post.name}<h6>{post.description}</h6></h2>
  <div class="card-body">
    <h6 class="card-title">Виды помощи: {post.listThings}</h6>
    <p class="clip">{post.region}</p>
    <p><small>Email: {post.email}</small></p> 
    <p><small>Телефое: {post.phone} {post.nameBoss}  </small></p> 
    <p><small>Адрес: {post.city} {post.address} </small></p> 

  </div>
</div>
    );
  }


  export default Post
