import React, { useEffect, useState, useContext,createContext} from "react";
import s from './Header.module.css'
import axios from "axios";
import {Context} from "./../../index";
import jwt_decode from "jwt-decode";
import {check, login, registration} from './../http/userApi'
import {NavLink, useNavigate} from "react-router-dom";
import {observer} from "mobx-react-lite";



const Registration=observer(()=>{
   const user=useContext(Context)

   const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

  
    const click = async () => {
        try {
            let data;
                data = await registration(email, password);
                console.log(data)
                window.location.reload()

        } catch (e) {
          alert(e.response)
        }

    }

    


    return <header className={s.header}>
                <div>
                <form>
                <div class="mb-3">
    <label for="exampleInputEmail1" class="form-label">Email address</label>
    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"
           value={email}
           onChange={e => setEmail(e.target.value)}
    ></input>
    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div class="mb-3">
    <label for="exampleInputPassword1" class="form-label">Password</label>
    <input type="password" class="form-control" id="exampleInputPassword1"
      value={password}
      onChange={e => setPassword(e.target.value)}
    ></input>
  </div>
                     
                        
                        </form>
                        <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal" onClick={click}>Заркгистрироваться</button>
                      
           


<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Подтверлите почтуу</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
      перейлите на почту и подтвердите аккаунт
      </div>
    
    </div>
  </div>
</div>

                </div>
    </header>
})

export default Registration
