import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { $authHost } from "../../http";
import ListAssist from "./ListAssist";
import ListNeedhelp from "./ListNeedhelp";
import { Spinner } from "react-bootstrap";
import { useEffect } from "react";
import { aaa } from "../../http/feth";
import { observer } from "mobx-react-lite";
const a=true

const Profil = observer(() => {
   
      
      
        const [loading, setLoading] = useState(true)
        const [persons, setpersons] = useState([])
        const [nhs, setnhs] = useState([])

useEffect(() => {
    aaa.then(
        data => {
            setpersons(data)
            
        })
}, [])
   /* componentDidMount() {
        $authHost.get(`http://localhost:5000/api/getAsistPerson`)
            .then(res => {
                const persons = res.data;
                this.setState({ persons });

            })
        $authHost.get(`http://localhost:5000/api/getNeedHelpPerson`)
            .then(res => {
                const nhs = res.data;
                this.setState({ nhs });
            })
        
    }
    if (a) {
        return <Spinner animation={"grow"} />
      }*/
    

        return (<div>

            <br></br><div class="d-grid gap-2 d-md-flex justify-content-md-end">
                <NavLink to={'/addcanhelp'}><button class="btn btn-primary" type="button">Добавить пост в категории "Могу помочь"</button></NavLink>
                <NavLink to={'/addneedhelp'}><button class="btn btn-primary" type="button">Добавить пост в категории "Нужна помошь"</button></NavLink>
            </div>
            <div>
                <h3>Ваши посты в категории "Могу помочь"</h3>
                <ul>
                    {this.state.persons == 0 ? <h1>У вас нет постов в категории "Могу помочь"</h1> : this.state.persons.map(person => <ListAssist person={person} />)}
                </ul>
            </div>

            <div>
                <h3>Ваши посты в категории "Нужна помошь"</h3>
                <ul>
                    {this.state.nhs == 0 ? <h1>У вас нет постов в категории "Нужна помочь"</h1> : this.state.nhs.map(nh => <ListNeedhelp nh={nh} />)}
                </ul>
            </div>
        </div>
        )
    }

)
