import PostHelp from "./posthelpa";
const Post=(props)=>{const params = useParams();
const prodId = params.id;}
export default class ListHelp extends React.Component {
    state = {
      persons: []
    }
  
    componentDidMount() {
      axios.get(`http://localhost:5000/api/getOnePointHelp/62d1819a9c431060ab969c40`)
        .then(res => {
          const persons = res.data;
          this.setState({ persons });
        })
    }
  
    render() {
      return (
       
       

     
<div class="card" > 
<h2 class="card-header">{ this.state.persons.name}<h6>{ this.state.persons.name}</h6></h2>
<div class="card-body">
  <h6 class="card-title">Виды помощи: {props.person.listThings}</h6>
  <p class="clip">{props.person.region}</p>
  <p><small>Email: {props.person.email}</small></p> 
  <p><small>Телефое: {props.person.phone} {props.person.nameBoss}  </small></p> 
  <p><small>Адрес: {props.person.city} {props.person.address} </small></p> 

  <a href={'/gum/'+props.person._id} class="btn btn-primary">Подробнее</a>
</div>
</div>
