import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchOneDevice, postComment } from './../http/feth'

const Post = (props) => {
     const { id } = useParams()
     const [devise, setDevise] = useState([])
     const [comment, setComment]=useState()
     const [data, setData]=useState()


     useEffect(() => {
          fetchOneDevice(id).then(data => setDevise(data))
     }, [])


const addComment=async()=>{
     setData( new Date())
     alert(data)
await postComment(id,data,comment)
}


     return (
<div>
          <div class="card">
               <div className="f">
                    <h2 class="card-title">{devise.name}<h6>{devise.description}</h6></h2>
                    <h6 class="card-title">Виды помощи: {devise.listThings}</h6>
                    <p class="clipp">{devise.region}</p>
                    <p><small>Email: {devise.email}</small></p>
                    <p><small>Телефое: {devise.phone} {devise.nameBoss}  </small></p>
                    <p><small>Адрес: {devise.city} {devise.address} </small></p>

               </div>
          </div>
          <input type="email" class="form-control" id="exampleFormControlInput1"
           value={comment}
           onChange={e => setComment(e.target.value)}
          ></input>
          
          <button class="btn btn-primary me-md-2" type="button" onClick={addComment}>Сохранить</button>

          </div>
     );
}

export default Post
