import axios from 'axios'
import React, { useEffect, useState, useContext,createContext} from "react";
import { NavLink } from 'react-router-dom';
import CanHelp from '../../posthelper/canhelp';
import g from './../../posthelper/module.css'



     const AddCanHelp=(props)=>{

        const REACT_APP_API_URL='http://localhost:5000/'

        const $host = axios.create({
            baseURL: REACT_APP_API_URL
        })
    
        const add = async (email,name,city,description,phone) => {
            const de = await $host.post('api/addAsistant', {email,name,city,description,phone})
        }
        const [name, setName] = useState('')
        const [city, setCity] = useState('')
        const [description, setDescription] = useState('')
        const [phone, setPhone] = useState('')
        const [email, setEmail] = useState('')
                

        const click = async () => {
            try {
                let data;
                //alert(email)
                console.log(email,name,city,description,phone)
                    data = await add(email,name,city,description,phone);
            } catch (e) {
                //alert(e.response.data.message)
            }
    
        }   


       




        return (
            <div className='brd'>
            <div class="mb-3">
            <label for="exampleFormControlInput1" class="form-label">Могу помочь...</label>
            <input type="email" class="form-control" id="exampleFormControlInput1" placeholder="Чем вы можете помочь"

            value={name}
            onChange={e => setName(e.target.value)}></input>

            </div>
            <div class="mb-3">
            <label for="exampleFormControlInput1" class="form-label">Город</label>
            <input type="email" class="form-control" id="exampleFormControlInput1"

            value={city}
            onChange={e => setCity(e.target.value)}>
            </input>
            </div>
            <div class="mb-3">
            <label for="exampleFormControlTextarea1" class="form-label">Описание помощи</label>
            <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"
            value={description}
            onChange={e => setDescription(e.target.value)}>

            </textarea>
            </div>
            <div class="mb-3">
            <label for="exampleFormControlInput1" class="form-label">Телефон</label>
            <input type="email" class="form-control" id="exampleFormControlInput1"
            value={phone}
            onChange={e => setPhone(e.target.value)}>

            </input>
            </div>
            <div class="mb-3">
            <label for="exampleFormControlInput1" class="form-label">Email</label>
            <input type="email" class="form-control" id="exampleFormControlInput1"

            value={email}
            onChange={e => setEmail(e.target.value)}>
            </input>
            </div>

            <NavLink to={'/ch'}> 
            <div class="d-grid gap-2 d-md-flex justify-content-md-end">
            <button class="btn btn-primary me-md-2" type="button" onClick={click}>Добавить</button>
            </div></NavLink>
            </div>
    );
}
      
      export default AddCanHelp
