import React from "react";

import { useEffect, useState } from "react";
import axios from 'axios'
import { NavLink } from "react-router-dom";
import { deleteonepost } from "../../http/feth";

const ListNeedhelp=(props)=>{

  const deletepost = async (id) => {
    try {
      const a=props.nh._id
        let data;
        //console.log('oooooooooooooooo===='+props.nh._id)
        data = await deleteonepost (a);
        window.location.reload()

    } catch (e) {
        alert(e.response)
    }

}
//console.log('oooooooooooooooo===='+props.nh._id)

  console.log(props)
  return(
  <div class="card" > 
  <h2 class="card-header">{props.nh.name}</h2>
  <div class="card-body">
    <h6 class="card-title">Нужно: {props.nh.listThings}</h6>
    <p class="clip">{props.nh.description}</p>
    <p><small>Телефое: {props.nh.phone} </small></p> 
    <p><small>Адрес: {props.nh.city}</small></p> 
   

   <NavLink to={'/nh/'+props.nh._id}><button type="button" class="btn btn-primary">Подробнее</button></NavLink>
   <NavLink to={'/nhupdate/'+props.nh._id}><button type="button" class="btn btn-primary">Редактировать</button></NavLink>
  
   <button type="button" class="btn btn-primary" onClick={deletepost}>Удалить</button>

  </div>
</div>

  )}
export default ListNeedhelp
