import axios from 'axios'
import React, { useEffect, useState, useContext,createContext} from "react";
import { NavLink } from 'react-router-dom';
import { $authHost } from '../../http';
import CanHelp from '../../posthelper/canhelp';
import g from './../../posthelper/module.css'



     const AddNeedHelp=(props)=>{
        
    
        const add = async (name, secondName, phone, city,listThings ,description) => {
            const de = await $authHost.post('api/addNeedHelp', {name, secondName, phone, city,listThings ,description})
        }
        const [name, setName] = useState('')
        const [city, setCity] = useState('')
        const [description, setDescription] = useState('')
        const [phone, setPhone] = useState('')
        const [listThings, setlistThings] = useState('')
        const [secondName, setsecondName] = useState('')

                

        const click = async () => {
            try {
                let data;
                //alert(email)
                console.log(name, secondName, phone, city,listThings ,description)
                data = await add(name, secondName, phone, city,listThings ,description);

            } catch (e) {
                //alert(e.response.data.message)
            }
    
        }   


       




        return (
            <div className='brd'>
            <div class="mb-3">
            <label for="exampleFormControlInput1" class="form-label">Могу помочь...</label>
            <input type="email" class="form-control" id="exampleFormControlInput1" placeholder="Чем вы можете помочь"

            value={name}
            onChange={e => setName(e.target.value)}></input>

            </div>
            <div class="mb-3">
            <label for="exampleFormControlInput1" class="form-label">Город</label>
            <input type="email" class="form-control" id="exampleFormControlInput1"

            value={city}
            onChange={e => setCity(e.target.value)}>
            </input>
            </div>
            <div class="mb-3">
            <label for="exampleFormControlTextarea1" class="form-label">Описание помощи</label>
            <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"
            value={description}
            onChange={e => setDescription(e.target.value)}>

            </textarea>
            </div>
            <div class="mb-3">
            <label for="exampleFormControlInput1" class="form-label">Телефон</label>
            <input type="email" class="form-control" id="exampleFormControlInput1"
            value={phone}
            onChange={e => setPhone(e.target.value)}>

            </input>
            </div>
            <div class="mb-3">
            <label for="exampleFormControlInput1" class="form-label">listThings</label>
            <input type="email" class="form-control" id="exampleFormControlInput1"
            value={listThings}
            onChange={e => setlistThings(e.target.value)}>

            </input>
            </div>
            <div class="mb-3">
            <label for="exampleFormControlInput1" class="form-label">secondName</label>
            <input type="email" class="form-control" id="exampleFormControlInput1"

            value={secondName}
            onChange={e => setsecondName(e.target.value)}>
            </input>
            </div>
            <div>
  <label for="formFileLg" class="form-label">Large file input example</label>
  <input class="form-control form-control-lg" id="formFileLg" type="file"></input>
</div>

            <NavLink to={'/nha'}> 
            <div class="d-grid gap-2 d-md-flex justify-content-md-end">
            <button class="btn btn-primary me-md-2" type="button" onClick={click}>Добавить</button>
            </div></NavLink> 
             { window.location.reload() }
            </div>
          
    );
}
      
      export default AddNeedHelp
